table.scripts:
BEGIN
   EXECUTE IMMEDIATE 'REVOKE SELECT ON {owner}.{prefix}TB{base} FROM {schema}';
EXCEPTION
   WHEN OTHERS THEN
      IF SQLCODE = -1927 THEN
         DBMS_OUTPUT.PUT_LINE('{schema}: Privilege not granted, skipping...');
      ELSE
         DBMS_OUTPUT.PUT_LINE('{schema}: Error - ' || SQLERRM);
      END IF;
END;

