triggers.body:
If INSERTING
    Then
        If GFKSJPA.GETID() Is Not Null
        Then
            :NEW.{table}_SURROGATE_ID  := GFKSJPA.GETID();
            GFKSJPA.SETID(Null);
        Else
            :NEW.{table}_SURROGATE_ID  := {owner}.{prefix}SE{base}.NEXTVAL;
        End If;
        If GFKSJPA.GETVERSION() Is Not Null
        Then
            :NEW.{table}_VERSION  := GFKSJPA.GETVERSION();
            GFKSJPA.SETVERSION(Null);
        Else
            :NEW.{table}_VERSION  := 0;
        End If;
    Else
        If GFKSJPA.GETVERSION() Is Not Null
        Then
            :NEW.{table}_VERSION  := GFKSJPA.GETVERSION();
            GFKSJPA.SETVERSION(Null);
        Elsif :NEW.{table}_VERSION = :OLD.{table}_VERSION
        Then
            :NEW.{table}_VERSION  := :OLD.{table}_VERSION + 1;
        End If;
    End If;
